import React, { Component } from 'react';

import './c_project.css'
import 'semantic-ui-css/semantic.min.css'
import { v4 as uuidv4 } from 'uuid';
//For muilt select
import { Dropdown } from 'semantic-ui-react'








class create_project extends Component{
    constructor(props) {
        super(props)

        this.state = {
            projectid: uuidv4(),
            projectname:'',
            master:'',
            projectUser:[]
        }
    }
    changeHandler = (e) =>{
        this.setState({[e.target.name]: e.target.value})
    }


    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
    } 

    getBird = (event, {value}) => {
        console.log(value);
        let bird_name = event.target.textContent;
        console.log(bird_name);
        var new_var = this.state.projectUser.concat(bird_name);
        this.setState({projectUser: new_var})
    }

    options = [
        { key: 'angular', text: 'Angular', value: 'angular' },
        { key: 'css', text: 'CSS', value: 'css' },
        { key: 'design', text: 'Graphic Design', value: 'design' },
        { key: 'ember', text: 'Ember', value: 'ember' },
        { key: 'html', text: 'HTML', value: 'html' },
        { key: 'ia', text: 'Information Architecture', value: 'ia' },
        { key: 'javascript', text: 'Javascript', value: 'javascript' },
        { key: 'mech', text: 'Mechanical Engineering', value: 'mech' },
        { key: 'meteor', text: 'Meteor', value: 'meteor' },
        { key: 'node', text: 'NodeJS', value: 'node' },
        { key: 'plumbing', text: 'Plumbing', value: 'plumbing' },
        { key: 'python', text: 'Python', value: 'python' },
        { key: 'rails', text: 'Rails', value: 'rails' },
        { key: 'react', text: 'React', value: 'react' },
        { key: 'repair', text: 'Kitchen Repair', value: 'repair' },
        { key: 'ruby', text: 'Ruby', value: 'ruby' },
        { key: 'ui', text: 'UI Design', value: 'ui' },
        { key: 'ux', text: 'User Experience', value: 'ux' },
      ]



render(){
    const {projectid,projectname,master} = this.state;
    return (
        <>

            <div className="container">

            <form onSubmit={this.submitHandler}>
                <p className="deftvalue">Project ID</p>
                <div className="group"> 
                <input 
                type="text"
                name="projectid" 
                required 
                disabled
                defaultValue={projectid}
                //value={uuidv4()}
                onChange={this.changeHandler}
                />
                <span className="highlight"></span>
                <span className="bar"></span>

                </div>

                <div className="group">      
                <input 
                type="text" 
                name="projectname" 
                required
                value={projectname}
                onChange={this.changeHandler}
                />
                <span className="highlight"></span>
                <span className="bar"></span>
                <label>Project Name</label>
                </div>

                <div className="group">      
                <input 
                type="text" 
                name="master" 
                required
                value={master}
                onChange={this.changeHandler}
                />
                <span className="highlight"></span>
                <span className="bar"></span>
                <label>Master</label>
                </div>
                <div>
                <p className="deftvalue">Project User</p>
                <Dropdown placeholder='Project User' 
                fluid multiple selection search
                name='projectUser'
                options={this.options}
                onChange={this.getBird} />
                </div>



                <button type = "submit">Submit</button>



            </form>



            </div>








        </>
      );
}


}


export default create_project
