import React, { Component } from 'react';

import './c_project.css'

import { v4 as uuidv4 } from 'uuid';
//For muilt select
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
const animatedComponents = makeAnimated();

const useroptions = [
    { value: '1123', label: 'Dennis' },
    { value: '1129', label: 'Ben' },
    { value: '1128', label: 'Eunji' },
    { value: '1125', label: 'bomi' }
  ]


const userGroupoptions = [
{ value: '1123', label: 'Dennis' },
{ value: '1129', label: 'photographerGroup1' },
{ value: '1130', label: 'photographerGroup2' },


]





class create_project extends Component{
    constructor(props) {
        super(props)

        this.state = {
            projectid: uuidv4(),
            projectname:'',
            master:'',
            projectUser:[],
            projectGroup:[],
            projectStatus: true,
            fileDir: uuidv4(),

        }
    }

    changeHandler = (e) =>{
        this.setState({[e.target.name]: e.target.value})
    }
    handle_M_SelectChange = projectUser => {
        var projectUser_val = '';
        if(projectUser.length > 0){
            for(var i=0; i<projectUser.length;i++){
                console.log(projectUser[i].value)
                projectUser_val +=projectUser[i].value
            }
        }else{
            console.log("NULL")
        }
        console.log("User Result :" + projectUser_val)
        this.setState({projectUser_val});

    };
    handle_M_SelectChangeOnGroup = projectGroup => {
        this.setState({projectGroup });

    };


    submitHandler = e => {
       // const axios = require('axios');
        e.preventDefault()
        console.log(this.state)

        // axios.post('/project/posts', 
        //     this.state
        // )
        //   .then(function (response) {
        //     console.log(response);
        //   })
        //   .catch(function (error) {
        //     console.log(error);
        //   });
    } 








render(){
    const {projectid,projectname,master} = this.state;

    return (
        <>

            <div className="container">

            <form onSubmit={this.submitHandler}>
                <p className="deftvalue">Project ID</p>
                <div className="group"> 
                <input 
                type="text"
                name="projectid" 
                required 
                disabled
                defaultValue={projectid}
                //value={uuidv4()}
                onChange={this.changeHandler}
                />
                <span className="highlight"></span>
                <span className="bar"></span>

                </div>

                <div className="group">      
                <input 
                type="text" 
                name="projectname" 
                required
                value={projectname}
                onChange={this.changeHandler}
                />
                <span className="highlight"></span>
                <span className="bar"></span>
                <label>Project Name</label>
                </div>

                <div className="group">      
                <input 
                type="text" 
                name="master" 
                required
                value={master}
                onChange={this.changeHandler}
                />
                <span className="highlight"></span>
                <span className="bar"></span>
                <label>Master</label>
                </div>
                <div>
                <p className="deftvalue">Project User</p>
                <Select 
                isMulti
                closeMenuOnSelect={false}
                components={animatedComponents}
                onChange={this.handle_M_SelectChange}
                options={useroptions} />
                </div>
                <div>
                <p className="deftvalue">Project Group</p>
                <Select 
                isMulti
                closeMenuOnSelect={false}
                components={animatedComponents}
                onChange={this.handle_M_SelectChangeOnGroup}
                options={userGroupoptions} />
                </div>

                <br/><br/>




                <button type = "submit">Submit</button>



            </form>



            </div>








        </>
      );
}


}


export default create_project
